CREATE   VIEW [dim].[v_DimProduct] AS SELECT p.ProductID AS [ProductID], ISNULL(p.ProductName, '') AS [ProductName], ISNULL(p.ProductCode, '') AS [ProductCode], ISNULL(p.Brand, '') AS [Brand], p.UnitPrice AS [UnitPrice], p.UnitCost AS [UnitCost], p.Weight AS [Weight], ISNULL(p.Color, '') AS [Color], ISNULL(p.Size, '') AS [Size], p.LaunchDate AS [LaunchDate], p.DiscontinuedDate AS [DiscontinuedDate], p.IsActive AS [IsActive], ISNULL(pc.CategoryName, '') AS [CategoryName], ISNULL(pc.CategoryDescription, '') AS [CategoryDescription], pc.ParentCategoryID AS [ParentCategoryID] FROM stg.Products p LEFT JOIN stg.ProductCategories pc ON p.CategoryID = pc.CategoryID
GO
